function filterDataByTable(inputData, tableValue) {
    let count = 0;
    const filteredData = [];

    for (const item of inputData) {
        if (count >= 500) {
            break; 
        } else if (item.table == tableValue) {
            const time = item._time;
            const value = item._value;
            filteredData.push({ "_time": time, "value": value });
            count++;
        }
    }

    return filteredData;
}

var airHumi = filterDataByTable(data, 0);
var airTemp = filterDataByTable(data, 1);
var lux = filterDataByTable(data, 2);
var soilHumi = filterDataByTable(data, 3);
var waterLevel = filterDataByTable(data, 4);


var mergedData = airHumi.reduce((result, item) => {
    let airTemp_c = airTemp.find(data => data._time == item._time).value;
    let airHumi_c = airHumi.find(data => data._time == item._time).value;

    let date_c = new Date(item._time);
    var day = date_c.getDate();
    var month = date_c.getMonth() + 1; 
    var year = date_c.getFullYear();
    var hours = date_c.getHours().toString().padStart(2, '0');
    var minutes = date_c.getMinutes().toString().padStart(2, '0');
    var seconds = date_c.getSeconds().toString().padStart(2, '0');
    var formattedTime = hours + ':' + minutes + ':' + seconds;
    var formattedDate = (day < 10 ? '0' : '') + day + '-' + (month < 10 ? '0' : '') + month + '-' + year;


    var mergedItem = {
        date: formattedDate,
        time: formattedTime,
        airHumi: airHumi_c.toFixed(2),
        airTemp: airTemp_c.toFixed(2),
        lux: lux.find(data => data._time == item._time).value,
        soilHumi: soilHumi.find(data => data._time == item._time).value,
        waterLevel: waterLevel.find(data => data._time == item._time).value
    };
    result.push(mergedItem);
    return result;
}, []);

msg.payload = mergedData;
return msg;

